import React from "react";  
import Navigation from "./Components/navigation";
import Section from './Components/section';
import Navbar2 from "./Components/Navbar2";
import Signin from './Components/Signin.js'
import Signup from './Components/Signup'
import Signin_black from './Components/Signin_black'
import Signup_dark from './Components/Signup_dark'
import Navbar2_dark from './Components/navbar2_dark'

class App extends React.Component{
  constructor(){
    super()
    this.state={
      route:'home',
      navbar_status : "navbar_home",
      nav_bg : 'white',
      form_bg : "white",
      email : '',
      password : ''
    }
  }
  clicking = ()=>{
    window.location.href="https://www.wazirx.com"
  }

  first2 = ()=>{
    window.location.href="https://wazirx.com/exchange/"
  }

  second2 = ()=>{
    window.location.href="https://wazirx.com/p2p"
  }

   third2= ()=>{
    this.setState({
      route:'login',
      navbar_status : "navbar_login"
    })
  }

   fourth2= ()=>{
    this.setState({
      route:'signup',
      navbar_status : "navbar_signup",
    })
  }


  changingtheme = () =>{
    this.setState({
      nav_bg : 'black',
      form_bg : "black"
    })
    document.body.style.backgroundColor='#111622'
  }

  changingtheme2 = () =>{
    this.setState({
      nav_bg : 'white',
      form_bg : "white"
    })
    document.body.style.backgroundColor='#f0f2f5'
  }

  email_change = (event)=>{
    this.setState({
      email : event.target.value
    })
  }

   password_change = (event)=>{
    this.setState({
      password : event.target.value
    })
  }

  onSubmitSignup = ()=>{
    fetch('http://127.0.0.1:3001/signup', {
      'method':'post',

      'body':JSON.stringify({
        email : this.state.email,
        password : this.state.password
      })
    }).then(res => res.json())
  }

  render(){
    return(
      <div className="App">
        {this.state.route=='home' && this.state.navbar_status=='navbar_home'
        ?<div>
              <Navigation click2={this.clicking} first={this.first2} second = {this.second2} third = {this.third2} fourth = {this.fourth2} />
              <Section clicksignup={this.fourth2} />
              </div>
              :<div>              
                 {this.state.route=='login' && this.state.navbar_status=='navbar_login'
                 ?<div>
                  {this.state.nav_bg=='white'&& this.state.form_bg=='white'
                  ?<div>
                    <Navbar2 toblack={this.changingtheme} value23={'SIGNUP'} />
                    <Signin/>
                  </div>
                  :<div>
                    <Navbar2_dark towhite={this.changingtheme2} value23={'SIGNUP'} />
                    <Signin_black />
                  </div>
                  }
                 </div>
                 :<div>
                       {this.state.route=='signup' && this.state.navbar_status=='navbar_signup'
                       ?<div>
                        {this.state.nav_bg=='white'&& this.state.form_bg=='white'
                        ?<div>
                          <Navbar2 toblack={this.changingtheme} value23={'LOGIN'}/>
                          <Signup changeemail={this.email_change} changepassword={this.password_change}  submitting={this.onSubmitSignup} />
                        </div>
                        :<div>
                          <Navbar2_dark towhite={this.changingtheme2} value23={'LOGIN'} />
                          <Signup_dark />
                        </div>
                        }
                       </div>
                       :<div>Not Exist error</div>
                       }
                 </div>
                 }
              </div>
}
          </div>
    )
  }
}

export default App;
