import React from "react";  
import Navigation from "./Components/navigation";
import Section from './Components/section';
import Navbar2 from "./Components/Navbar2";
import Signin from './Components/Signin.js'
import Signup from './Components/Signup'
import Signin_black from './Components/Signin_black'
import Navbar2_dark from './Components/navbar2_dark'

class App extends React.Component{
  constructor(){
    super()
    this.state={
      route:'home',
      navbar_status : "navbar_home",
      nav_bg : 'white',
      loginform_bg : "white"
    }
  }
  clicking = ()=>{
    window.location.href="https://www.wazirx.com"
  }

  first2 = ()=>{
    window.location.href="https://wazirx.com/exchange/"
  }

  second2 = ()=>{
    window.location.href="https://wazirx.com/p2p"
  }

   third2= ()=>{
    this.setState({
      route:'login',
      navbar_status : "navbar_login"
    })
  }

   fourth2= ()=>{
    window.location.href="https://wazirx.com/signup?source=menubar"
  }
  changetosignup = () =>{
    this.setState({
      route:'signup',
      navbar_status : "navbar_signup",
    })
  }

  changingtheme = () =>{
    this.setState({
      nav_bg : 'black',
      loginform_bg : "black"
    })
    document.body.style.backgroundColor='#111622'
  }

  changingtheme2 = () =>{
    this.setState({
      nav_bg : 'white',
      loginform_bg : "white"
    })
    document.body.style.backgroundColor='#f0f2f5'
  }

  render(){
    return(
      <div className="App">
        {this.state.route=="home" && this.state.navbar_status=='navbar_home'
        ?<div>
        <Navigation click2={this.clicking} first={this.first2} second = {this.second2} third = {this.third2} fourth = {this.fourth2} />
        <Section clicksignup={this.fourth2} />
        </div>
        :<div>
          {this.state.route=="login" && this.state.navbar_status=='navbar_login' 
          ?<div>
            {this.state.nav_bg=='white' && this.state.loginform_bg=='white'
            ?<div>
              <Navbar2 bah={this.changetosignup} towhite = {this.changingtheme}/>
              <Signin/>
            </div>
            :<div>
            {this.state.route=="signup" && this.state.navbar_status=='navbar_signup' 
            ?<div>              
            <Navbar2 bah={this.changetosignup} towhite = {this.changingtheme}/>
            <Signup />
            </div>
            :<div>
            <Navbar2_dark toblack={this.changingtheme2}/>
            <Signin_black/>
            </div>
            }
            </div>
          }
          </div>
          :<div>This does not exist</div>
          }
          :<div>
          {this.state.route=="signup" && this.state.navbar_status=='navbar_signup' 
          ?<div>
          <Navbar2 towhite = {this.changingtheme}/>
          <Signup />
          </div>
          :<div>Error</div>
          }
          </div>
        </div>
    }
      </div>
    )
  }
}

export default App;
